function calculate(){var a,c=document.getElementById("type"),b=document.getElementById("years");b&&b.value&&(b=parseInt(b.value,10));if(c&&c.value&&b&&0<b){switch(c.value){case "basic":a=10;break;case "premium":a=15;break;case "gold":a=20;break;case "platinum":a=25}a*=b;switch(b){case 1:a*=1;break;case 2:case 3:a*=.9;break;case 4:a*=.85;break;default:a*=.8}document.getElementById("cost").value="$"+a.toFixed(2)}else document.getElementById("cost").value="Please enter valid values.";return!1}
function init(){document.getElementById("theForm").onsubmit=calculate}window.onload=init;